import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/tasks', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTasks(res.data);
      setError('');
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setError('Failed to fetch tasks');
    } finally {
      setLoading(false);
    }
  };

  const updateTaskStatus = async (taskId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `http://localhost:5000/api/tasks/${taskId}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchTasks();
    } catch (error) {
      console.error('Error updating task:', error);
      alert('Failed to update task');
    }
  };

  const deleteTask = async (taskId) => {
    if (!window.confirm('Are you sure you want to delete this task?')) {
      return;
    }
    
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/tasks/${taskId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
      alert('Failed to delete task');
    }
  };

  if (loading) {
    return (
      <div className="glass-card">
        <div className="loading-container">
          <span className="loading"></span>
          <p>Loading your tasks...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="glass-card">
        <div className="error-container">
          <span className="error-icon">‚ö†Ô∏è</span>
          <p>{error}</p>
          <button className="modern-btn btn-secondary" onClick={fetchTasks}>
            üîÑ Try Again
          </button>
        </div>
      </div>
    );
  }

  if (tasks.length === 0) {
    return (
      <div className="glass-card">
        <div className="empty-state">
          <div className="empty-icon">üìù</div>
          <h3>No tasks yet!</h3>
          <p>Create your first task to get started</p>
        </div>
      </div>
    );
  }

  return (
    <div className="glass-card">
      <div className="card-header">
        <h3 className="card-title">üìã Your Tasks ({tasks.length})</h3>
        <p className="card-subtitle">Manage and track your progress</p>
      </div>
      
      <div className="tasks-container">
        {tasks.map((task, index) => (
          <div 
            key={task._id} 
            className="task-card"
            style={{ animationDelay: `${index * 0.1}s` }}
          >
            <div className="task-header">
              <h4 className="task-title">{task.title}</h4>
              <div className="task-actions">
                <select
                  value={task.status}
                  onChange={(e) => updateTaskStatus(task._id, e.target.value)}
                  className="status-select"
                >
                  <option value="pending">‚è≥ Pending</option>
                  <option value="in-progress">üöÄ In Progress</option>
                  <option value="completed">‚úÖ Completed</option>
                </select>
                <button
                  onClick={() => deleteTask(task._id)}
                  className="action-btn btn-delete"
                  title="Delete task"
                >
                  üóëÔ∏è
                </button>
              </div>
            </div>
            
            {task.description && (
              <p className="task-description">{task.description}</p>
            )}
            
            <div className="task-meta">
              <span className="task-date">
                üìÖ Created: {new Date(task.createdAt).toLocaleDateString()}
              </span>
              <span className={`status-badge status-${task.status}`}>
                {task.status.charAt(0).toUpperCase() + task.status.slice(1)}
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TaskList;
