import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import TaskList from './components/TaskList';
import AddTask from './components/AddTask';
import Login from './components/Login';
import Register from './components/Register';
import AdminDashboard from './components/AdminDashboard';

const App = () => {
  const [user, setUser] = useState(null);
  const [showLogin, setShowLogin] = useState(true);
  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');
    
    if (token && savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setRefresh(!refresh);
  };

  const handleTaskAdded = () => {
    setRefresh(!refresh);
  };

  const switchToRegister = () => {
    setShowLogin(false);
  };

  const switchToLogin = () => {
    setShowLogin(true);
  };

  if (!user) {
    return (
      <div style={{ padding: '20px' }}>
        <h1 style={{ textAlign: 'center', color: '#333' }}>Task Manager</h1>
        {showLogin ? (
          <Login onLogin={handleLogin} switchToRegister={switchToRegister} />
        ) : (
          <Register onRegister={() => {}} switchToLogin={switchToLogin} />
        )}
      </div>
    );
  }

  // Check if user is admin
  const isAdmin = user.role === 'admin';

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center', 
        marginBottom: '20px',
        padding: '10px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px'
      }}>
        <h1 style={{ margin: 0, color: '#333' }}>Task Manager</h1>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          <span>Welcome, {user.name}!</span>
          {isAdmin && (
            <span style={{
              backgroundColor: '#28a745',
              color: 'white',
              padding: '4px 8px',
              borderRadius: '12px',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>
              ADMIN
            </span>
          )}
          <button 
            onClick={handleLogout}
            style={{
              padding: '8px 16px',
              backgroundColor: '#dc3545',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Logout
          </button>
        </div>
      </div>
      
      {isAdmin ? (
        // Admin Dashboard
        <AdminDashboard />
      ) : (
        // Regular User Interface
        <>
          <AddTask onTaskAdded={handleTaskAdded} />
          <TaskList key={refresh} />
        </>
      )}
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
